mapper.select:
SELECT tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, dlvy_addr, dlvy_request, tran_status_code, order_date, dlvy_date
FROM transaction
WHERE tran_No = #{value}

SELECT tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, dlvy_addr, dlvy_request, tran_status_code, order_date, dlvy_date
FROM transaction
WHERE prod_No = #{value}

mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, dlvy_addr = #{divyAddr}, dlvy_request = #{divyRequest}, dlvy_date = #{divyDate},
WHERE tran_no = #{tranNo}
